import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Subscription } from 'rxjs'; // Add this import statement

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {
  formData = {
    name: '',
    email: '',
    message: ''
  };
  submitted = false;
  subscription!: Subscription; // Add this property

  constructor(private http: HttpClient) { }

  onSubmit() {
    this.subscription = this.http.post('http://localhost:3000/contact', this.formData)
      .subscribe(
        response => {
          console.log('Message sent successfully');
          this.submitted = true;
        },
        error => {
          console.error('Error sending message:', error);
        }
      );
  }

  ngOnDestroy() {
    // Unsubscribe to avoid memory leaks
    this.subscription.unsubscribe();
  }
}
<section class="contact-us">
    <h2>Contact us</h2>
    <p>Get in touch with us to discuss your project requirements.</p>
    <form (submit)="onSubmit()">
      <div class="form-group">
        <label for="name">Name</label>
        <input type="text" class="form-control" id="name" name="name" [(ngModel)]="formData.name" required>
    </div>
      <div class="form-group">
        <label for="email">Email</label>
        <input type="email" class="form-control" id="email" name="email" [(ngModel)]="formData.email" required>
    </div>
      <div class="form-group">
        <label for="message">Message</label>
        <textarea class="form-control" id="message" name="message" [(ngModel)]="formData.message" required></textarea>
    </div>
      <button type="submit" class="btn btn-primary" >Send message</button>
    </form>
  </section>
