import { Component } from '@angular/core';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {
  name!: string;
  email!: string;
  message!: string;
  onSubmit() {
    const formData = {
      name: this.name,
      email: this.email,
      message: this.message
    };
    console.log(formData); // or send this data to server using HTTP request
  }
}
<section class="contact-us">
    <h2>Contact us</h2>
    <p>Get in touch with us to discuss your project requirements.</p>
    <form (submit)="onSubmit()">
      <div class="form-group">
        <label for="name">Name</label>
        <input type="text" class="form-control" id="name" name="name" [(ngModel)]="name" required>
    </div>
      <div class="form-group">
        <label for="email">Email</label>
        <input type="email" class="form-control" id="email" name="email" [(ngModel)]="email" required>
    </div>
      <div class="form-group">
        <label for="message">Message</label>
        <textarea class="form-control" id="message" name="message" [(ngModel)]="message" required></textarea>
    </div>
      <button type="submit" class="btn btn-primary" >Send message</button>
    </form>
  </section>
