import { Component, HostListener, ElementRef } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  sticky: boolean = false;
  header: any;

  constructor(private el: ElementRef) {}

  @HostListener('window:scroll', [])
  onWindowScroll() {
    const { nativeElement } = this.el;
    const heroHeight = nativeElement.querySelector('.hero').offsetHeight;
    const offset = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;

    if (offset > heroHeight) {
      this.sticky = true;
    } else {
      this.sticky = false;
    }
  }

  ngAfterViewInit() {
    this.header = document.querySelector('.site-header');
  }
}
<main>
  <app-header [sticky]="sticky" class="site-header"></app-header>
  <section class="hero">
    <div class="hero-content">
      <h1>Your digital transformation partner</h1>
      <p>We are a full-service web development agency</p>
      <a href="contact" class="btn btn-primary">Get started</a>
    </div>
  </section>

  <app-services></app-services>
  <app-about></app-about>
  <app-considerations></app-considerations>
  <app-faq></app-faq>
  <app-contact></app-contact>
</main>
